import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate  } from "react-router-dom";

const Register = () => {
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [confPassowrd, setConfPassowrd] = useState('');
    const [msg, setMsg] = useState('');
    const navigate = useNavigate ();

    const Register = async (e) => {
        e.preventDefault();
        try {
            await axios.post('http://localhost:5000/register', {
                name: name,
                email: email,
                password: password,
                confPassowrd: confPassowrd
            });
            navigate.push("/");
        } catch (error) {
            setMsg(error.response.data.msg);
        }
    }

    return (
        <section className="hero is-fullheight is-bold">
            <div className="hero-body">
                <div className="container has-text-centered">
                    <div className="column is-4 is-offset-4">
                        <h3 className="title has-text-black">Register</h3>
                        <p className="subtitle has-text-black">Join with us.</p>
                        <div className="box">
                            <form onSubmit={Register}>
                                <p className="has-text-centered is-danger">{msg}</p>
                                <div className="field">
                                    <div className="control">
                                        <input className="input" type="text" placeholder="Your Fullname" autoFocus value={name} onChange={(e) => setName(e.target.value)} />
                                    </div>
                                </div>

                                <div className="field">
                                    <div className="control">
                                        <input className="input" type="text" placeholder="Your Email" value={email} onChange={(e) => setEmail(e.target.value)} />
                                    </div>
                                </div>

                                <div className="field">
                                    <div className="control">
                                        <input className="input" type="password" placeholder="Your Password" value={password} onChange={(e) => setPassword(e.target.value)} />
                                    </div>
                                </div>

                                <div className="field">
                                    <div className="control">
                                        <input className="input" type="password" placeholder="Confirm Your Password" value={confPassowrd} onChange={(e) => setConfPassowrd(e.target.value)} />
                                    </div>
                                </div>

                                <button className="button is-block is-primary is-fullwidth">Login</button>
                            </form>
                        </div>
                    </div>
                </div>
            </div>
        </section>
    )
}

export default Register
