import React, {useState} from 'react';
import axios from 'axios';
import { useNavigate  } from "react-router-dom";


const Login = () => {
        const [email, setEmail] = useState('');
        const [password, setPassword] = useState('');
        const [msg, setMsg] = useState('');
        const navigate = useNavigate ();
    
        const Auth = async (e) => {
            e.preventDefault();
            try {
                await axios.post('http://localhost:5000/login', {
                    email: email,
                    password: password,
                });
                navigate.push("/dashboard");
            } catch (error) {
                setMsg(error.response.data.msg);
            }
        }
    
    return (
        <section className="hero is-fullheight is-bold">
            <div className="hero-body">
                <div className="container has-text-centered">
                    <div className="column is-4 is-offset-4">
                        <h3 className="title has-text-black">Login</h3>
                        <p className="subtitle has-text-black">Please log in to proceed.</p>
                        <div className="box">
                            <form onSubmit={Login}>
                                <p className="has-text-centered">{msg}</p>
                                <div className="field">
                                    <div className="control">
                                        <input className="input" type="text" placeholder="Your Email" autoFocus value={email} onChange={(e) => setEmail(e.target.value)}/>
                                    </div>
                                </div>

                                <div className="field">
                                    <div className="control">
                                        <input className="input" type="password" placeholder="Your Password" value={password} onChange={(e) => setPassword(e.target.value)}/>
                                    </div>
                                </div>

                                <button className="button is-block is-primary is-fullwidth">Login</button>
                            </form>
                        </div>
                    </div>
                </div>
            </div>
        </section>
    )
}

export default Login
