import React, { useEffect, useState } from 'react';
import axios from 'axios';
import jwtDecode from 'jwt-decode';
import { useNavigate } from 'react-router-dom';
import Navbar from './Navbar';
import 'jquery';

const Dashboard = () => {
    const [name, setName] = useState('');
    const [token, setToken] = useState('');
    const navigate = useNavigate();

    useEffect(() => {
        refreshToken();
    }, []);

    const refreshToken = async () => {
        try {
            const response = await axios.get('http://localhost:5000/token');
            const decoded = jwtDecode(response.data.accessToken);

            setToken(response.data.accessToken);
            setName(decoded.name);

            // You can use the decoded.exp for further expiration checks if needed

        } catch (error) {
            if (error.response) {
                navigate('/');
            }
        }
    };

    const axiosJWT = axios.create();

    axiosJWT.interceptors.request.use(async (config) => {
        const currentDate = new Date();
        const decoded = jwtDecode(token);

        if (decoded.exp * 1000 < currentDate.getTime()) {
            await refreshToken();
            config.headers.Authorization = `Bearer ${token}`;
        }

        return config;
    }, (error) => {
        return Promise.reject(error);
    });

    const getUsers = async () => {
        const response = await axiosJWT.get('http://localhost:5000/users');
        console.log(response.data);
    };

    useEffect(() => {
        $(document).ready(() => {
            $('#userTable').DataTable();
        });
    }, []);

    return (
        <div>
            <Navbar />
            <div className="container">
                <h1 className="title">Welcome {name}</h1>
                <button className="button is-info" onClick={getUsers}>
                    Get Users
                </button>

                <table id="userTable" className="display">
                    <thead>
                        <tr>
                            <th>User ID</th>
                            <th>Name</th>
                            <th>Email</th>
                        </tr>
                    </thead>
                    <tbody>{/* Data will be populated here dynamically */}</tbody>
                </table>
            </div>
        </div>
    );
};

export default Dashboard;
