import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Navigate } from 'react-router-dom';
import Navbar from './Navbar';
import { jwtDecode } from 'jwt-decode'; // Import jwtDecode specifically

const Dashboard = () => {
    const [token, setToken] = useState('');

    useEffect(() => {
        refreshToken();
    }, []);

    const refreshToken = async () => {
        try {
            const response = await axios.get('http://localhost:5000/token');
            const newToken = response.data.accessToken;
            setToken(newToken);
        } catch (error) {
            // Handle error, e.g., redirect to login page or show an error message
            console.error('Token refresh failed:', error);
        }
    };

    if (!token) {
        // Redirect or handle unauthenticated user (e.g., show a login page)
        return <Navigate to="/login" />;
    }

    return (
        <div>
            <Navbar />
            <div className="container">
                <h1 className="title">Welcome gaes</h1>
            </div>
        </div>
    );
};

export default Dashboard;
