import React, { useEffect, useState } from 'react'
import axios from 'axios';
import { jwtDecode } from 'jwt-decode';
import { useNavigate } from 'react-router-dom';
import Navbar from './Navbar'

const Dashboard = () => {
    const [name, setName] = useState('');
    const [id, setId] = useState('');
    const [location, setLocation] = useState(null);
    const [device, setDevice] = useState('');
    const [token, setToken] = useState('');
    const [expire, setExpire] = useState('');
    const [attendance, setAttendance] = useState([]);
    const [msg, setMsg] = useState('');
    const navigate = useNavigate();
    const [currentTime, setCurrentTime] = useState(new Date());

    const getLocation = () => {
        if (navigator.geolocation) {
            navigator.geolocation.getCurrentPosition(
                (position) => {
                    const { latitude, longitude } = position.coords;
                    setLocation(`${latitude},${longitude}`);
                },
                (error) => {
                    console.error('Error getting location:', error.message);
                }
            );
        } else {
            console.error('Geolocation is not supported by this browser.');
        }
    };

    useEffect(() => {
        refreshToken();
        setDevice(navigator.platform);
        getLocation();

         // Update current time every second
    const intervalId = setInterval(() => {
        setCurrentTime(new Date());
      }, 1000);
  
      return () => {
        // Cleanup interval on component unmount
        clearInterval(intervalId);
      };
    }, [])

    const refreshToken = async () => {
        try {
            const response = await axios.get('http://localhost:5000/token');
            setToken(response.data.accessToken);
            const decoded = jwtDecode(response.data.accessToken);
            setName(decoded.name);
            setId(decoded.id);
            setExpire(decoded.exp);
        } catch (error) {
            if (error.response) {
                navigate('/')
            }
        }
    }

    const axiosJWT = axios.create();

    axiosJWT.interceptors.request.use(async (config) => {
        const currentDate = new Date();
        if (expire * 1000 < currentDate.getTime()) {
            const response = await axios.get('http://localhost:5000/token');
            config.headers.Authorization = `Bearer ${response.data.accessToken}`;
            setToken(response.data.accessToken);
            const decoded = jwtDecode(response.data.accessToken);
            setName(decoded.name);
            setExpire(decoded.exp);
        }
        return config;
    }, (error) => {
        return Promise.reject(error);
    });

    const API_BASE_URL = 'http://localhost:5000';

    const fetchAttendance = async () => {
        try {
            const response = await axios.get(`${API_BASE_URL}/attendance`, {
                headers: {
                    Authorization: `Bearer ${token}`
                }
            });
            setAttendance(response.data);
        } catch (error) {
            console.error('Error fetching attendance:', error);
        }
    };

    const checkIn = async (e) => {
        e.preventDefault();
        try {
            const currentDate = new Date();
            const formattedDate = currentDate.toISOString();
            await axios.post(`${API_BASE_URL}/post-attendance`, {
                userId: id,
                date: formattedDate,
                checkIn: formattedDate.split('T')[1].split('.')[0],
                checkOut: "",
                location: location,
                device: device
            });
        } catch (error) {
            console.error('Error during check-in:', error);
            if (error.response) {
                setMsg(error.response.data.msg);
            }
        }
    };

    const checkOut = async (e) => {
        e.preventDefault();
        try {
            const currentDate = new Date();
            const formattedDate = currentDate.toISOString();
            await axios.post(`${API_BASE_URL}/post-attendance`, {
                userId: id,
                date: formattedDate,
                checkIn: '',
                checkOut: formattedDate.split('T')[1].split('.')[0],
                location: location,
                device: device
            });
        } catch (error) {
            console.error('Error during check-in:', error);
            if (error.response) {
                setMsg(error.response.data.msg);
            }
        }
    };


    return (
        <div>
            <Navbar />
            <div className="container">
                <h1 className="title">Welcome {name}</h1>
                <p>{msg}</p>
                <div className="is-flex">
                    <button className="button is-warning" onClick={checkIn}>Check-In</button>
                    {isAfterNoon && (
                        <button className="button is-success ml-2" onClick={checkOut}>Check-Out</button>
                    )}
                    <button className="button is-info ml-2" onClick={fetchAttendance}>Get Attendance</button>
                </div>

                <table className='table is-striped is-fullwidth'>
                    <thead>
                        <tr>
                            <th>No</th>
                            <th>Name</th>
                            <th>Email</th>
                            <th>Check In</th>
                            <th>Check Out</th>
                            <th>Location</th>
                            <th>Device</th>
                        </tr>
                    </thead>
                    <tbody>
                        {attendance.map((att, index) => (
                            <tr key={att.id}>
                                <td>{index + 1}</td>
                                <td>{att.user.name}</td>
                                <td>{att.user.email}</td>
                                <td>{att.checkIn}</td>
                                <td>{att.checkOut}</td>
                                <td>{att.location}</td>
                                <td>{att.device}</td>
                            </tr>
                        ))}
                    </tbody>

                </table>
            </div>
        </div>
    )
}

export default Dashboard
